window.addEventListener('load',function(){
    if(this.localStorage.getItem("email")===null){
        localStorage.setItem("email","");
    }
    if(this.localStorage.getItem("email")==""){
        window.location.replace("./login.html");
    }
})

window.addEventListener('load',function(){
    var ch;
    var alphabet=document.getElementById("alphabet");
    var inner=alphabet.innerHTML;
    for(var i=65;i<=90;i++){
        ch=String.fromCharCode(i);
        inner+=`<button class="btn btn-info contact" onclick="buttondisplay(this.innerHTML)">${ch}</button>`
    }
    alphabet.innerHTML=inner;
})

window.addEventListener('load',function(){
    loadleft()
    var email=localStorage.getItem("email");
    var index=employee_details.find(obj => {return obj.email == email});
    inner=`
    <img id="profile_image" src="${index.image_url}" onclick="dropdown()">
    <div class="right_text" id="logout" onclick="logout()"></div>
    <h3 class="right_text">${index.preferred_name}</h3>
    <h3 id="bluetext" class="right_text">Welcome,</h3>`;
    document.getElementById("profile").innerHTML=inner;
})

function dropdown(){
    var inner=`<h6>LogOut</h6>`;
    if(document.getElementById("logout").innerHTML==''){
        document.getElementById("logout").innerHTML=inner;
    }
    else{
        document.getElementById("logout").innerHTML='';
    }
}

function logout(){
    localStorage.setItem("email",'');
    window.location.replace("./login.html");
    
}

function loadleft(){
    department_list={};
    office_list={};
    jobrole_list={};
    hidden=``;
    for(var i of employee_details){
        if(typeof(department_list[i.department])==="undefined"){
            department_list[i.department]=1;
        }
        else{
            department_list[i.department]+=1;
        }
        if(typeof(jobrole_list[i.job_title])==="undefined"){
            jobrole_list[i.job_title]=1;
        }
        else{
            jobrole_list[i.job_title]+=1;
        }
        if(typeof(office_list[i.office])==="undefined"){
            office_list[i.office]=1;
        }
        else{
            office_list[i.office]+=1;
        }
    }
    var inner=document.getElementById("left_bar");
    var innertext=`<p><b>Departments</b></p>`;
    for(var i in department_list){
        innertext+=`<p id="Department" onclick="leftdisplay('${i}',id)">${i}(${department_list[i]})</p>`;
    }
    innertext+=`<p><b>Offices</b></p>`;
    for(var i in office_list){
        innertext+=`<p id="office" onclick="leftdisplay('${i}',id)">${i}(${office_list[i]})</p>`;
    }
    innertext+=`<p><b>Job Titles</b></p>`;
    var min=0;
    for(var i in jobrole_list){
        if(min<3){
            innertext+=`<p id="Job_title" onclick="leftdisplay('${i}',id)">${i}(${jobrole_list[i]})</p>`;
            min++;
        }
        else{
            hidden+=`<p id="Job_title" onclick="leftdisplay('${i}',id)">${i}(${jobrole_list[i]})</p>`;
        }
    }
    innertext+=`<p class="bluetext" onclick="jobroles()" id="view_more">view more</p>`;
    inner.innerHTML=innertext;
}
function jobroles(){
    document.getElementById("view_more").style.display="none";
    var inner=document.getElementById("left_bar");
    inner.innerHTML+=hidden;
}

function previewFile() {
    const file = document.getElementById("file").files[0];
    const reader = new FileReader();

    reader.addEventListener("load", () => {
       imageurl=reader.result;
    }, false);
  
    if (file) {
      reader.readAsDataURL(file);
    }
  }

function newemployee(){
    var form=document.getElementById("popup");
    form.addEventListener("click", function(event){
    event.preventDefault();
    });
    var prefferedname=form.elements[1].value;
    var firstname=form.elements[2].value;
    var lastname=form.elements[3].value;
    var email=form.elements[4].value;
    var jobtitle=form.elements[5].value;
    var office_name=form.elements[6].value;
    var department=form.elements[7].value;
    var phonenumber=form.elements[8].value;
    var skypeid=form.elements[9].value;
    var alpha=/^[a-zA-Z][a-zA-Z ]*$/;
    var mail=/^[a-zA-Z0-9][a-zA-Z0-9_.-]*@[a-zA-Z0-9]+(.[a-zA-Z]+)+$/;
    var numeric=/^[0-9]{10}$/;
    if(alpha.test(prefferedname)){
        if(alpha.test(firstname)){
            if(alpha.test(lastname)){
                if(mail.test(email)){
                    if(alpha.test(jobtitle)){
                        if(numeric.test(phonenumber)){
                            var employee={
                                preferred_name:prefferedname,
                                first_name:firstname,
                                last_name:lastname,
                                email:email,
                                job_title:jobtitle,
                                office:office_name,
                                department:department,
                                phone_number:phonenumber,
                                skype_id:skypeid,
                                image_url:imageurl
                            }
                            if(edit==0){
                                employee_details.push(employee);
                            }
                            else{
                                edit=0;
                                var index=employee_details.findIndex(obj => (obj.email==email));
                                employee_details[index]=employee;
                            }
                            hide();
                            loadleft();
                            displayAllEmployees();
                            localStorage.setItem("employee_details",JSON.stringify(employee_details));
                            document.getElementById("popup").reset();
                        }
                        else{
                            alert("Invalid Phone Number");
                            document.getElementById("phone_number").focus();
                        }
                    }
                    else{
                        alert("Invalid Job Title");
                        document.getElementById("job_title").focus();
                    }
                }
                else{
                    alert("Invalid Email");
                    document.getElementById("email").focus();
                }
            }
            else{
                alert("Invalid Last name");
                document.getElementById("last_name").focus();
            }
        }
        else{
            alert("Invalid First name");
            document.getElementById("first_name").focus();
        }
    }
    else{
        alert("Invalid Preffered name");
        document.getElementById("preffered_name").focus();
    }
    
}

function displayemployee(){
    var filter=(document.getElementById("filter").value).toLowerCase();
    var search=(document.getElementById("search").value).toLowerCase();
    const inner=document.getElementById("employee_cards");
    let innertext="";
    var input = document.getElementById("search");
    input.addEventListener("change", function() {
        search=(document.getElementById("search").value).toLowerCase();
    });
    var count=0;
    if(search!=''){
        var val;
        for(let i of employee_details){
            val=i[filter].toLowerCase();
            if(val.startsWith(search)){
                innertext+=`
                <div class= "col-lg-3 card" onclick="displaycard('${i.preferred_name}')">
                    <div><img src="${i.image_url}" class="img"></div>
                    <h6><small>${i.first_name}</small></h6>
                    <p><small>${i.job_title}</small></p>
                    <p><small>${i.department}</small></p>
                    <i class="fa-solid fa-phone"></i>
                    <i class="fa-solid fa-envelope"></i>
                    <i class="fa-solid fa-message"></i>
                    <i class="fa-solid fa-star"></i>
                    <i class="fa-solid fa-heart"></i>
                </div>`;
                count++;
            }
        }
    }
    else{
        for(let i of employee_details){
            innertext+=`
                <div class= "col-lg-3 card" onclick="displaycard('${i.preferred_name}')">
                    <div><img src="${i.image_url}" class="img"></div>
                    <h6>${i.first_name}</h6>
                    <p><small>${i.job_title}</small></p>
                    <p><small>${i.department}</small></p>
                    <i class="fa-solid fa-phone"></i>
                    <i class="fa-solid fa-envelope"></i>
                    <i class="fa-solid fa-message"></i>
                    <i class="fa-solid fa-star"></i>
                    <i class="fa-solid fa-heart"></i>
                </div>`;
                count++;
        }
    }
    if(count!=0){
        inner.innerHTML=innertext;
    }
    else{
        inner.innerHTML=`<div class="no_employee"><h2>No Match</h2></div>`
    }
}

