function displaycard(name){
    var i=employee_details.find(obj => {return obj.preferred_name == name});
    innertext=`
    <div class="col-lg-6 popup_image">
        <img src="${i.image_url}" width="338px" height="339px">
    </div>
    <div class="col-lg-6 popup_text">
        <h4>${i.preferred_name}<div id="right_buttons"><button type="button" onclick="editDetails('${i.email}')"><i class="fa-solid fa-user-pen"></i></button><button type="button" onclick="hidecard()">&cross;</button></div></h4>
        <div>
            <h6>Primary Details</h6>
            <p><small>First Name:${i.first_name}</small></p>
            <p><small>Last Name:${i.last_name}</small></p>
        </div>
        <div>
            <h6>Job Details</h6>
            <p><small>Job Title:${i.job_title}</small></p>
            <p><small>Department:${i.department}</small></p>
            <p><small>Office:${i.office}</small></p>
        </div>
        <div>
            <h6>Contact Details</h6>
            <p><small>Email:${i.email}</small></p>
            <p><small>Phone Number:${i.phone_number}</small></p>
            <p><small>Skype Id:${i.skype_id}</small></p>
        </div>
    </div>
    `;
    var html1=document.getElementById("popup_card");
    html1.innerHTML=innertext;
    document.getElementById("popup_card").style.display="block";
}

function hidecard(){
    document.getElementById("popup_card").style.display = "none";
}

function editDetails(email){
    hidecard();
    addemployee();
    var index=employee_details.find(obj => {return obj.email == email});
    var form=document.getElementById("popup");
    form.elements[1].value=index.preferred_name;
    form.elements[2].value=index.first_name;
    form.elements[3].value=index.last_name;
    form.elements[4].value=index.email;
    form.elements[5].value=index.job_title;
    form.elements[6].value=index.office;
    form.elements[7].value=index.department;
    form.elements[8].value=index.phone_number;
    form.elements[9].value=index.skype_id;
    imageurl=index.image_url;
    edit=1;
    identifier=index.email;
}